mapper.select:
/*获得当前用户每一条最新的私信列表*/
select 
from message
where id in(
    /*拿到一个用户的所有私信列表的最新一条私信的id*/
    select max(id)
    from message
    where status != 2
    and from_id != 1
    /*当前用户可能是发送方，也可能是接收方，但是都拿出来*/
    and (from_id = #{userId} or to_id = #{userId})
    group by conversation_id
)
order by id desc
limit #{offset}, #{limit}

select count(id)
from message
where status = 0
and from_id != 1
and to_id = #{userId}

    and conversation_id = #{conversationId}

mapper.update:
update message set status = #{status}
where id in

    #{id}
